import React, { useEffect, useState } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import Dashboard from "./Dashboard";

const Profile = () => {
  const { user, isAuthenticated, getIdTokenClaims } = useAuth0();
  const [userRoles, setUserRoles] = useState([]);

  useEffect(() => {
    const fetchUserRoles = async () => {
      try {
        if (isAuthenticated) {
          const idTokenClaims = await getIdTokenClaims();
          const roles = idTokenClaims["http://localhost:3000/roles"] || [];
          setUserRoles(roles);

          // Pass roles to Hasura
          const token = await getIdTokenClaims(); // Get the ID token
          const response = await fetch('YOUR_HASURA_ENDPOINT', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
              'x-hasura-role': roles.join(','), // Pass roles as comma-separated string
              'Authorization': `Bearer ${token.__raw}` // Pass the ID token
            },
            body: JSON.stringify({}) // You can optionally include a body if needed
          });

          // Handle response as needed
          if (!response.ok) {
            throw new Error('Failed to pass roles to Hasura');
          }
        }
      } catch (error) {
        console.error('Error fetching user roles:', error);
      }
    };

    fetchUserRoles();
  }, [getIdTokenClaims, isAuthenticated]);

  return (
    isAuthenticated && (
      <div>
        <img src={user.picture} alt={user.name} />
        <h2>{user.name}</h2>
        <p>{user.email}</p>
        <p>Roles: {userRoles.join(", ")}</p> {/* Display user roles */}
        <Dashboard userRoles={userRoles} />
      </div>
    )
  );
};

export default Profile;
