import React, { useState, useEffect } from 'react';
import { useQuery, gql } from '@apollo/client';
import './Dashboard.css'; // Import the CSS file

const GET_DATA = gql`
  query MyQuery($offset: Int!, $limit: Int!) {
    Table2(offset: $offset, limit: $limit) {
      id
      bnb_name
      host_id
      host_name
      neighborhood_group
      neighborhood
      latitude
      longitude
      room_type
      price
      minimum_nights
      number_of_reviews
      last_review
      reviews_per_month
      calculated_host
    }
    Table2_aggregate {
      aggregate {
        totalCount: count
      }
    }
  }
`;

function Dashboard() {
  const [page, setPage] = useState(1);
  const limit = 10; // Number of items per page

  const { loading, error, data, fetchMore } = useQuery(GET_DATA, {
    variables: {
      offset: (page - 1) * limit,
      limit: limit
    }
  });

  useEffect(() => {
    if (page > 1) {
      fetchMore({
        variables: {
          offset: (page - 1) * limit,
          limit: limit
        },
        updateQuery: (prev, { fetchMoreResult }) => {
          if (!fetchMoreResult) return prev;
          return {
            ...prev,
            Table2: fetchMoreResult.Table2,
          };
        },
      });
    }
  }, [page, fetchMore, limit]);

  useEffect(() => {
    if (page < Math.ceil(data?.Table2_aggregate?.aggregate?.totalCount / limit)) {
      fetchMore({
        variables: {
          offset: page * limit,
          limit: limit
        },
      });
    }
  }, [page, data, fetchMore, limit]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  const totalRows = data.Table2_aggregate.aggregate.totalCount;
  const totalPages = Math.ceil(totalRows / limit);

  const handlePrevious = () => {
    setPage(prevPage => Math.max(prevPage - 1, 1));
  };

  const handleNext = () => {
    setPage(prevPage => Math.min(prevPage + 1, totalPages));
  };

  return (
    <div className="center-container">
      <div className="dashboard-container">
        <h2>Table 2</h2>
        <div className="table-container">
          <table className="data-table">
            <thead>
              <tr>
                <th>ID</th>
                <th>BNB Name</th>
              </tr>
            </thead>
            <tbody>
              {data.Table2.map(item => (
                <tr key={item.id}>
                  <td className="left-align">{item.id}</td>
                  <td className="left-align">{item.bnb_name}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <div className="pagination">
          <button onClick={handlePrevious} disabled={page === 1}>Previous</button>
          <span className="page-number">{`Page ${page} of ${totalPages}`}</span>
          <button onClick={handleNext} disabled={page === totalPages}>Next</button>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
