import React, { useState, useEffect, useRef } from 'react';
import { useQuery, gql } from '@apollo/client';
import './Dashboard.css'; // Import the CSS file
import { useAuth0 } from '@auth0/auth0-react'; // Import Auth0 hook

const GET_DATA = gql`
  query MyQuery($offset: Int!, $limit: Int!, $userRoles: [String!]!) {
    Table3(role: { _in: $userRoles } },offset: $offset, limit: $limit) {
      id
      bnb_name
      host_id
      host_name
      neighborhood_group
      neighborhood
      latitude
      longitude
      room_type
      price
      minimum_nights
      number_of_reviews
      last_review
      reviews_per_month
      calculated_host_listings_count
      availability_365
    }
    Table3_aggregate {
      aggregate {
        totalCount: count
      }
    }
  }
`;

function Dashboard() {
  const [page, setPage] = useState(1);
  const limit = 10; // Number of items per page
  const tableRef = useRef(null); // Create a ref for the table element
  const { isAuthenticated, loginWithRedirect, user } = useAuth0(); // Auth0 hook
// Function to map Auth0 roles to Hasura roles
const mapAuth0RolesToHasuraRoles = (user) => {
  // Retrieve user roles from Auth0 profile
  const auth0Roles = isAuthenticated && user ? user.roles : [];
  
  // Map Auth0 roles to corresponding Hasura roles
  // Example mapping logic
  return auth0Roles.map(auth0Role => {
    if (auth0Role === 'admin') return 'admin';
    if (auth0Role === 'user') return 'user';
    // Handle other roles as needed
  });
};
  const { loading, error, data, fetchMore } = useQuery(GET_DATA, {
    variables: {
      offset: (page - 1) * limit,
      limit: limit,
      userRoles: mapAuth0RolesToHasuraRoles()
    }
  });

  useEffect(() => {
    if (!isAuthenticated) {
      loginWithRedirect(); // Redirect to login if not authenticated
    }
  }, [isAuthenticated, loginWithRedirect]);

  useEffect(() => {
    if (tableRef.current) {
      tableRef.current.scrollIntoView({ behavior: 'smooth', block: 'start' }); // Scroll to the table when it's rendered
    }
  }, [data]);

  useEffect(() => {
    if (page > 1) {
      fetchMore({
        variables: {
          offset: (page - 1) * limit,
          limit: limit
        },
        updateQuery: (prev, { fetchMoreResult }) => {
          if (!fetchMoreResult) return prev;
          return {
            ...prev,
            Table3: fetchMoreResult.Table3,
          };
        },
      });
    }
  }, [page, fetchMore, limit]);

  useEffect(() => {
    if (page < Math.ceil(data?.Table3_aggregate?.aggregate?.totalCount / limit)) {
      fetchMore({
        variables: {
          offset: page * limit,
          limit: limit
        },
      });
    }
  }, [page, data, fetchMore, limit]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  const totalRows = data.Table3_aggregate.aggregate.totalCount;
  const totalPages = Math.ceil(totalRows / limit);

  const handlePrevious = () => {
    setPage(prevPage => Math.max(prevPage - 1, 1));
  };

  const handleNext = () => {
    setPage(prevPage => Math.min(prevPage + 1, totalPages));
  };


  return (
    <div className="center-container">
      <div className="dashboard-container">
        <div className="dashboard-header">
          <h2>"Table3"</h2>
        </div>
        <div className="table-container">
          <table className="data-table" ref={tableRef}>
            <thead>
              <tr>
                <th>ID</th>
                <th>BNB Name</th>
                <th>Host ID</th>
                <th>Host Name</th>
                <th>Neighborhood Group</th>
                <th>Neighborhood</th>
                <th>Latitude</th>
                <th>Longitude</th>
                <th>Room Type</th>
                <th>Price</th>
                <th>Minimum Nights</th>
                <th>Number of Reviews</th>
                <th>Last Review</th>
                <th>Reviews Per Month</th>
                <th>Calculated Host Listings Count</th>
                <th>Availability 365</th>
              </tr>
            </thead>
            <tbody>
              {data.Table3 && data.Table3.map(item => (
                <tr key={item.id}>
                  <td className="left-align">{item.id}</td>
                  <td className="left-align">{item.bnb_name}</td>
                  <td className="left-align">{item.host_id}</td>
                  <td className="left-align">{item.host_name}</td>
                  <td className="left-align">{item.neighborhood_group}</td>
                  <td className="left-align">{item.neighborhood}</td>
                  <td className="left-align">{item.latitude}</td>
                  <td className="left-align">{item.longitude}</td>
                  <td className="left-align">{item.room_type}</td>
                  <td className="left-align">{item.price}</td>
                  <td className="left-align">{item.minimum_nights}</td>
                  <td className="left-align">{item.number_of_reviews}</td>
                  <td className="left-align">{item.last_review}</td>
                  <td className="left-align">{item.reviews_per_month}</td>
                  <td className="left-align">{item.calculated_host_listings_count}</td>
                  <td className="left-align">{item.availability_365}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <div className="pagination">
          <button onClick={handlePrevious} disabled={page === 1}>Previous</button>
          <span className="page-number">{`Page ${page} of ${totalPages}`}</span>
          <button onClick={handleNext} disabled={page === totalPages}>Next</button>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
