// App.js

import React from 'react';
import { ApolloClient, InMemoryCache, ApolloProvider, createHttpLink } from '@apollo/client';
import { setContext } from '@apollo/client/link/context';
import Dashboard from './components/Dashboard';
import './App.css'; // Import the CSS file

// Replace 'YOUR_HASURA_GRAPHQL_ENDPOINT' and 'YOUR_JWT_TOKEN' with your actual values
const GRAPHQL_ENDPOINT = 'https://clean-wasp-50.hasura.app/v1/graphql';
const JWT_TOKEN = 'guest';

const httpLink = createHttpLink({
  uri: GRAPHQL_ENDPOINT,
});

const authLink = setContext((_, { headers }) => {
  return {
    headers: {
      ...headers,
      authorization: `Bearer ${JWT_TOKEN}`,
    },
  };
});

const client = new ApolloClient({
  link: authLink.concat(httpLink),
  cache: new InMemoryCache(),
});

function App() {
  return (
    <ApolloProvider client={client}>
      <div className="app-container">
        <h1 className="app-heading">Dashboard</h1>
        <Dashboard />
      </div>
    </ApolloProvider>
  );
}

export default App;
