import React from 'react';
import { ApolloClient, InMemoryCache, ApolloProvider, createHttpLink } from '@apollo/client';
import { setContext } from '@apollo/client/link/context';
import { Auth0Provider, useAuth0 } from '@auth0/auth0-react'; // Import Auth0Provider and useAuth0
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Home from './components/Home';
import Profile from './components/Profile';
import Callback from './components/Callback';

// Auth0 configuration
const domain = 'dev-zxj8jftof7s1z62p.us.auth0.com';
const clientId = 'w5nGHjMbQs4ogoJB1qCyJy1iwAT3BevD';
const audience = 'https://clean-wasp-50.hasura.app/v1/graphql'; // Hasura GraphQL endpoint
const redirectUri = 'http://localhost:3000';

const httpLink = createHttpLink({
  uri: 'https://clean-wasp-50.hasura.app/v1/graphql', // Hasura GraphQL endpoint
});

// Create an auth link that adds the Auth0 access token to the request headers
const authLink = setContext((_, { headers }) => {
  const token = localStorage.getItem('auth0Token');
  return {
    headers: {
      ...headers,
      authorization: token ? `Bearer ${token}` : '',
    },
  };
});

const client = new ApolloClient({
  link: authLink.concat(httpLink),
  cache: new InMemoryCache(),
});

function LoginButton() {
  const { loginWithRedirect, isAuthenticated } = useAuth0();

  return (
    <button onClick={() => loginWithRedirect()} disabled={isAuthenticated}>
      {isAuthenticated ? 'Logged In' : 'Log In'}
    </button>
  );
}

function LogoutButton() {
  const { logout } = useAuth0();

  return (
    <button onClick={() => logout({ logoutParams: { returnTo: window.location.origin } })}>
      Log Out
    </button>
  );
}

function App() {
  return (
    <Auth0Provider
      domain={domain}
      clientId={clientId}
      audience={audience}
      redirectUri={redirectUri}
    >
      <ApolloProvider client={client}>
        <Router>
          <div>
            {/* Navigation */}
            <nav>
              <ul>
                <li>
                  <Link to="/">Home</Link>
                </li>
                <li>
                  <Link to="/profile">Profile</Link>
                </li>
                <li>
                  {/* Render LoginButton component */}
                  <LoginButton />
                </li>
                <li>
                  {/* Render LoginButton component */}
                  <LogoutButton />
                </li>
              </ul>
            </nav>

            {/* Routes */}
            <Routes>
              <Route exact path="/" element={<Home />} />
              <Route exact path="/profile" element={<Profile />} />
              <Route exact path="/callback" element={<Callback />} />
              <Route exact path="/dashboard" element={<Callback />} />
            </Routes>
          </div>
        </Router>
      </ApolloProvider>
    </Auth0Provider>
  );
}

export default App;
