import React, { useEffect, useState } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import Dashboard from "./Dashboard";

const Profile = () => {
  const { user, isAuthenticated, getIdTokenClaims } = useAuth0();
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    const fetchUserId = async () => {
      try {
        if (isAuthenticated) {
          const idTokenClaims = await getIdTokenClaims();
          const userId = idTokenClaims["http://localhost:3000/user_id"];
          if (userId) {
            setUserId(userId);

            // Pass user_id to Hasura
            const token = await getIdTokenClaims(); // Get the ID token
            const response = await fetch('https://clean-wasp-50.hasura.app/v1/graphql', {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json',
                'x-hasura-user-id': userId, // Pass user_id
                'Authorization': `Bearer ${token.__raw}` // Pass the ID token
              },
              body: JSON.stringify({}) // You can optionally include a body if needed
            });

            // Handle response as needed
            if (!response.ok) {
              throw new Error('Failed to pass user_id to Hasura');
            }
          } else {
            throw new Error('User ID not found in token claims');
          }
        }
      } catch (error) {
        console.error('Error fetching user ID:', error);
      }
    };

    fetchUserId();
  }, [getIdTokenClaims, isAuthenticated]);

  return (
    isAuthenticated && (
      <div>
        <img src={user.picture} alt={user.name} />
        <h2>{user.name}</h2>
        <p>{user.email}</p>
        {userId && <p>User ID: {userId}</p>}
      </div>
    )
  );
};

export default Profile;
